INSERT INTO dnd.condition (id, name, description) VALUES (9, 'Blinded', 'A blinded creature can’t see and automatically fails any ability check that requires sight.
INSERT INTO dnd.condition (id, name, description) VALUES (10, 'Deafened', 'A deafened creature can’t hear and automatically fails any ability check that requires hearing.');
INSERT INTO dnd.condition (id, name, description) VALUES (11, 'Exhaustion', 'Some special abilities and environmental hazards, such as starvation and the long-­term effects of freezing or scorching temperatures, can lead to a special condition called exhaustion. Exhaustion is measured in six levels. An effect can give a creature one or more levels of exhaustion, as specified in the effect’s description.
