INSERT INTO dnd.monster_actions (name, description, monster_id) VALUES ('Tail', 'Melee Weapon Attack: +6 to hit, reach 10 ft., one creature. Hit: 7 (1d8 + 3) slashing damage.', 68);
INSERT INTO dnd.monster_actions (name, description, monster_id) VALUES ('Moan', 'Each creature within 60 feet of the cloaker that can hear its moan and that isn''t an aberration must succeed on a DC 13 Wisdom saving throw or become frightened until the end of the cloaker''s next turn. If a creature''s saving throw is successful, the creature is immune to the cloaker''s moan for the next 24 hours.', 68);
INSERT INTO dnd.monster_actions (name, description, monster_id) VALUES ('Multiattack', 'The cloaker makes two attacks: one with its bite and one with its tail.', 68);
