INSERT INTO dnd.monster_traits (title, description, monster_id) VALUES ('Shielded Mind', 'The couatl is immune to scrying and to any effect that would sense its emotions, read its thoughts, or detect its location.', 74);
INSERT INTO dnd.monster_traits (title, description, monster_id) VALUES ('Innate Spellcasting', 'The couatl''s spellcasting ability is Charisma (spell save DC 14). It can innately cast the following spells, requiring only verbal components: At will: detect evil and good, detect magic, detect thoughts 3/day each: bless, create food and water, cure wounds, lesser restoration, protection from poison, sanctuary, shield 1/day each: dream, greater restoration, scrying', 74);
