INSERT INTO dnd.monster_traits (title, description, monster_id) VALUES ('Spellcasting', 'The mummy lord is a 10th-level spellcaster. Its spellcasting ability is Wisdom (spell save DC 17, +9 to hit with spell attacks). The mummy lord has the following cleric spells prepared:Cantrips (at will): sacred flame, thaumaturgy1st level (4 slots): command, guiding bolt, shield of faith2nd level (3 slots): hold person, silence, spiritual weapon3rd level (3 slots): animate dead, dispel magic4th level (3 slots): divination, guardian of faith5th level (2 slots): contagion, insect plague6th level (1 slot): harm', 207);
INSERT INTO dnd.monster_traits (title, description, monster_id) VALUES ('Rejuvenation', 'A destroyed mummy lord gains a new body in 24 hours if its heart is intact, regaining all its hit points and becoming active again. The new body appears within 5 feet of the mummy lord''s heart.', 207);
INSERT INTO dnd.legendary_actions (monster_id, description, name) VALUES (207, 'The mummy lord utters a blasphemous word. Each non-undead creature within 10 feet of the mummy lord that can hear the magical utterance must succeed on a DC 16 Constitution saving throw or be stunned until the end of the mummy lord''s next turn.', 'Blasphemous Word (Costs 2 Actions)');
INSERT INTO dnd.legendary_actions (monster_id, description, name) VALUES (207, 'Blinding dust and sand swirls magically around the mummy lord. Each creature within 5 feet of the mummy lord must succeed on a DC 16 Constitution saving throw or be blinded until the end of the creature''s next turn.', 'Blinding Dust');
