INSERT INTO dnd.monster_traits (title, description, monster_id) VALUES ('Innate Spellcasting', 'The solar''s spellcasting ability is Charisma (spell save DC 25). It can innately cast the following spells, requiring no material components: At will: detect evil and good, invisibility (self only) 3/day each: blade barrier, dispel evil and good, resurrection 1/day each: commune, control weather', 263);
INSERT INTO dnd.legendary_actions (monster_id, description, name) VALUES (263, 'The solar targets one creature it can see within 30 feet of it. If the target can see it, the target must succeed on a DC 15 Constitution saving throw or be blinded until magic such as the lesser restoration spell removes the blindness.', 'Blinding Gaze (Costs 3 Actions)');
